    // 路由表
    const router = new VueRouter({
      routes: [
        {
          path: '/index/:id', // 路由的路径
          name: 'index',  // 路由名称，可选属性，定义后可以用其实现跳转
          component: { // 路由显示的组件
            template: '首页：{{$route.params.id}}'  // 组件模板
          }
        },
        {
          path: '/news/:id/', // 通过路由传参
          name: 'news',
