      methods: {
        fn1() {
          // 通过路由名称跳转，配置params参数。
          this.$router.replace({ name: 'index', params: { id: Math.random() } });
        },
        fn2() {
          // 直接跳转路由地址，参数直接带在路径中。
          this.$router.push(`/news/${Math.random()}`);
