    img {
      width: 96.5px;
      height: 135px;
    }
    &:after {
      content: "▶";
      color: #fff;
      font-size: 14px;
      width: 22px;
      height: 22px;
      border-radius: 50%;
      border: 1px solid #fff;
      position: absolute;
      right: 12px;
      bottom: 8px;
      line-height: 22px;
      text-align: center;
      background-color: #333;
      opacity: 0.8;
    }
  }
}

.buy-btn {
  width: calc(100% - 30px);
  margin: 10px 15px;
  padding: 9px;
  font-size: 16px;
  color: #fff;
  text-align: center;
  border-radius: 4px;
  border: none;
  background-color: #e54847;
}
.movie-intro {
  text-align: center;
  width: 100%;
  padding: 3px 16px;
  > p {
    font-size: 14px;
    color: #666;
    overflow: hidden;
  }
  > .btn {
    width: 16px;
  }
}
.overflow {
  max-height: 60px;
}


